package gwtquery.plugins.droppable.client.draughtssample.resources;import com.google.gwt.core.client.GWT;import com.google.gwt.resources.client.ClientBundle;import com.google.gwt.resources.client.ImageResource;public interface DraughtsResources extends ClientBundle {	public DraughtsResources INSTANCE = GWT.create(DraughtsResources.class);	@Source("draughts.css")	public Css css();		@Source("white_piece.gif")	ImageResource whitePiece();		@Source("red_piece.gif")	ImageResource redPiece();	@Source("white_king.gif")	ImageResource whiteKing();		@Source("red_king.gif")	ImageResource redKing();	@Source("white_piece_th.gif")  ImageResource whitePieceThumb();    @Source("red_piece_th.gif")  ImageResource redPieceThumb();  @Source("white_king_th.gif")  ImageResource whiteKingThumb();    @Source("red_king_th.gif")  ImageResource redKingThumb();}